import React, { useRef, useEffect } from "react";
import "./SignIn.scss";

const SignIn = ({ wrapperRef, trigger, setSignInfromPopUp }) => {
  const useOutsideAlerter = ({ ref, setSignInfromPopUp }) => {
    useEffect(() => {
      const handleClickOutside = (event) => {
        if (ref.current && !ref.current.contains(event.target)) {
          console.log("You clicked outside of me!");
          // setSignInfromPopUp(false);
        }
      };
      // Bind the event listener
      document.addEventListener("mousedown", handleClickOutside);
      return () => {
        // Unbind the event listener on clean up
        document.removeEventListener("mousedown", handleClickOutside);
      };
    }, [ref, setSignInfromPopUp]);
  };
  // useOutsideAlerter(wrapperRef, setSignInfromPopUp);

  return;
  // trigger ?
  <>
    <div className="SignIn" ref={wrapperRef}>
      <div className="closeBtn">
        <button
          onClick={() => {
            setSignInfromPopUp(false);
          }}
        >
          X
        </button>
      </div>
      <h1>Sign In to Coachync</h1>
      <input type="text" placeholder="Email" name="email" />
      <input type="text" placeholder="Password" name="password" />
      <input type="submit" className="submit" />
    </div>
    {console.log(wrapperRef.current)}
  </>;
  // :
  // (
  //   ""
  // );
};

export default SignIn;
