import React, { useState, useRef, useEffect } from "react";
import {
  BrowserRouter as Router,
  Link,
  Navigate,
  Route,
  Routes,
} from "react-router-dom";
import "./App.scss";
import Navbar from "./components/general/Navbar";
import HeroSection from "./components/client/HeroSection";
import CoachingsOverview from "./components/client/CoachingsOverview";
import Benefits from "./components/client/Benefits";
import HowWeWork from "./components/client/HowWeWork";
import HappyStart from "./components/client/HappyStart";
import Footer from "./components/general/Footer";
import Coaches from "./components/Coaches";
import HomeBody from "./components/Home/HomeBody";
import SearchCoaches from "./components/Home/SearchCoaches";
import CoachProfile from "./components/coachProfile/CoachProfile";
import Chat from "./components/chat/Chat";
import SignIn from "./components/general/SignIn";
import SignUpFrom from "./components/general/SignUpForm";
import OutsideAlerter from "./components/general/OutsideAlerter";

function App() {
  const [signInFormPopUp, setSignInFormPopUp] = useState(false);
  const [signUpFormPopUp, setSignUpFormPopUp] = useState(false);
  const [opacity, setOpacity] = useState("100%");

  return (
    <Router>
      <div className="App" id="App">
        <Navbar
          style={{ opacity: opacity }}
          setOpacity={setOpacity}
          setSignInFormPopUp={setSignInFormPopUp}
          setSignUpFormPopUp={setSignUpFormPopUp}
        ></Navbar>
        <br />
        <Routes>
          <Route
            path="/"
            element={
              <>
                <SearchCoaches />
                <HomeBody
                  style={({ backgroundcolor: "green" }, { opacity: "10%" })}
                  setOpacity={setOpacity}
                />
                <Footer style={{ opacity: opacity }} setOpacity={setOpacity} />
              </>
            }
          ></Route>
          <Route
            path="/moreAboutUs"
            element={
              <>
                <HeroSection />
                <CoachingsOverview />
                <Benefits />
                <HowWeWork />
                <HappyStart />
                <Footer></Footer>
              </>
            }
          ></Route>
          <Route
            path="/Coaches"
            element={
              <>
                <Coaches />
                <Footer />
              </>
            }
          ></Route>
          <Route
            path="/search"
            element={
              <>
                <SearchCoaches />
                <HomeBody />
                <Footer />
              </>
            }
          ></Route>
          <Route
            path="/profile"
            element={
              <>
                <CoachProfile />
                <Footer />
              </>
            }
          ></Route>
        </Routes>

        <OutsideAlerter
          setSignInFormPopUp={setSignInFormPopUp}
          opacity={opacity}
          setOpacity={setOpacity}
        >
          <SignIn
            opacity={opacity}
            setOpacity={setOpacity}
            trigger={signInFormPopUp}
            setSignInFormPopUp={setSignInFormPopUp}
          ></SignIn>
        </OutsideAlerter>

        <OutsideAlerter
          opacity={opacity}
          setOpacity={setOpacity}
          setSignUpFormPopUp={setSignUpFormPopUp}
        >
          <SignUpFrom
            opacity={opacity}
            setOpacity={setOpacity}
            trigger={signUpFormPopUp}
            setSignUpFormPopUp={setSignUpFormPopUp}
          ></SignUpFrom>
        </OutsideAlerter>

        {/* 
        <CoachProfile />
        <Footer />
        <Navbar></Navbar>
        <Chat></Chat>
        <Footer />  */}
      </div>
    </Router>
  );
}

export default App;
