import React, { useState, useRef, useEffect } from "react";
import {
  BrowserRouter as Router,
  Link,
  Navigate,
  Route,
  Routes,
} from "react-router-dom";
import "./App.scss";
import Navbar from "./components/general/Navbar";
import HeroSection from "./components/client/HeroSection";
import CoachingsOverview from "./components/client/CoachingsOverview";
import Benefits from "./components/client/Benefits";
import HowWeWork from "./components/client/HowWeWork";
import HappyStart from "./components/client/HappyStart";
import Footer from "./components/general/Footer";
import SignUp from "./components/SignUp";
import HomeBody from "./components/Home/HomeBody";
import SearchCoaches from "./components/Home/SearchCoaches";
import CoachProfile from "./components/coachProfile/CoachProfile";
import Chat from "./components/chat/Chat";
import SignIn from "./components/coachProfile/SignIn";
import Outsider from "./components/general/Outsider";

function App() {
  const [signInfromPopUp, setSignInfromPopUp] = useState(false);
  const wrapperRef = useRef(null);
  const useOutsideAlerter = ({ ref, setSignInfromPopUp }) => {
    useEffect(() => {
      const handleClickOutside = (event) => {
        if (ref.current && !ref.current.contains(event.target)) {
          console.log("You clicked outside of me!");
          setSignInfromPopUp(false);
        }
      };
      // Bind the event listener
      document.addEventListener("mousedown", handleClickOutside);
      return () => {
        // Unbind the event listener on clean up
        document.removeEventListener("mousedown", handleClickOutside);
      };
    }, [ref, setSignInfromPopUp]);
  };

  return (
    <Router>
      <div className="App">
        <Navbar setSignInfromPopUp={setSignInfromPopUp}></Navbar>
        <br />
        <Routes>
          <Route
            path="/"
            element={
              <>
                <SearchCoaches />
                <HomeBody />
                <Footer />
              </>
            }
          ></Route>
          <Route
            path="/moreAboutUs"
            element={
              <>
                <HeroSection />
                <CoachingsOverview />
                <Benefits />
                <HowWeWork />
                <HappyStart />
                <Footer></Footer>
              </>
            }
          ></Route>
          <Route
            path="/SignUp"
            element={
              <>
                <SignUp />
                <Footer />
              </>
            }
          ></Route>
          <Route
            path="/search"
            element={
              <>
                <SearchCoaches />
                <HomeBody />
                <Footer />
              </>
            }
          ></Route>
          <Route
            path="/profile"
            element={
              <>
                <CoachProfile />
                <Footer />
              </>
            }
          ></Route>
        </Routes>

        <SignIn
          trigger={signInfromPopUp}
          setSignInfromPopUp={setSignInfromPopUp}
        ></SignIn>

        <button ref={wrapperRef} onclick={useOutsideAlerter}>
          {" "}
          Here
        </button>

        {/* 
        <CoachProfile />
        <Footer />
        <Navbar></Navbar>
        <Chat></Chat>
        <Footer />  */}
      </div>
    </Router>
  );
}

export default App;
