import React, { useState, useRef, useEffect } from "react";
import {
  BrowserRouter as Router,
  Link,
  Navigate,
  Route,
  Routes,
} from "react-router-dom";
import "./App.scss";
import Navbar from "./components/general/Navbar";
import HeroSection from "./components/client/HeroSection";
import CoachingsOverview from "./components/client/CoachingsOverview";
import Benefits from "./components/client/Benefits";
import HowWeWork from "./components/client/HowWeWork";
import HappyStart from "./components/client/HappyStart";
import Footer from "./components/general/Footer";
import SignUp from "./components/SignUp";
import HomeBody from "./components/Home/HomeBody";
import SearchCoaches from "./components/Home/SearchCoaches";
import CoachProfile from "./components/coachProfile/CoachProfile";
import Chat from "./components/chat/Chat";
import SignIn from "./components/coachProfile/SignIn";

function App() {
  const [signInfromPopUp, setSignInfromPopUp] = useState(false);

  // * Hook that alerts clicks outside of the passed ref
  const useOutsideAlerter = (ref) => {
    useEffect(() => {
      //  Alert if clicked on outside of element
      function handleClickOutside(event) {
        if (ref.current && !ref.current.contains(event.target)) {
          alert("You clicked outside of me!");
        }
      }
      // Bind the event listener
      document.addEventListener("mousedown", handleClickOutside);
      return () => {
        // Unbind the event listener on clean up
        document.removeEventListener("mousedown", handleClickOutside);
      };
    }, [ref]);
  };

  const OutsideAlerter = (props) => {
    const wrapperRef = useRef(null);
    useOutsideAlerter(wrapperRef);

    return <div ref={wrapperRef}>{props.children}</div>;
  };
  return (
    <Router>
      <div className="App">
        <Navbar setSignInfromPopUp={setSignInfromPopUp}></Navbar>
        <br />
        <Routes>
          <Route
            path="/"
            element={
              <>
                <SearchCoaches />
                <HomeBody />
                <Footer />
              </>
            }
          ></Route>
          <Route
            path="/moreAboutUs"
            element={
              <>
                <HeroSection />
                <CoachingsOverview />
                <Benefits />
                <HowWeWork />
                <HappyStart />
                <Footer></Footer>
              </>
            }
          ></Route>
          <Route
            path="/SignUp"
            element={
              <>
                <SignUp />
                <Footer />
              </>
            }
          ></Route>
          <Route
            path="/search"
            element={
              <>
                <SearchCoaches />
                <HomeBody />
                <Footer />
              </>
            }
          ></Route>
          <Route
            path="/profile"
            element={
              <>
                <CoachProfile />
                <Footer />
              </>
            }
          ></Route>
        </Routes>

        <SignIn
          trigger={signInfromPopUp}
          setSignInfromPopUp={setSignInfromPopUp}
        ></SignIn>
        {/* 
        <CoachProfile />
        <Footer />
        <Navbar></Navbar>
        <Chat></Chat>
        <Footer />  */}
      </div>
    </Router>
  );
}

export default App;
