import React, { useRef, useEffect } from "react";
import "./SignIn.scss";

const SignIn = ({ ref_prop, trigger, setSignInfromPopUp }) => {
  const useOutsideAlerter = ({ ref_prop, setSignInfromPopUp }) => {
    useEffect(() => {
      const handleClickOutside = (event) => {
        if (ref_prop.current && !ref_prop.current.contains(event.target)) {
          console.log("You clicked outside of me!");
          setSignInfromPopUp(false);
        }
      };
      // Bind the event listener
      document.addEventListener("mousedown", handleClickOutside);
      return () => {
        // Unbind the event listener on clean up
        document.removeEventListener("mousedown", handleClickOutside);
      };
    }, [ref_prop, setSignInfromPopUp]);
  };

  useOutsideAlerter(ref_prop, setSignInfromPopUp);

  return trigger ? (
    <div className="SignIn">
      <div class="closeBtn">
        <button
          onClick={() => {
            setSignInfromPopUp(false);
          }}
        >
          X
        </button>
      </div>
      <h1>Sign In to Coachync</h1>
      <input type="text" placeholder="Email" name="email" />
      <input type="text" placeholder="Password" name="password" />
      <input type="submit" className="submit" />
    </div>
  ) : (
    ""
  );
};

export default SignIn;
